// updated for TinyMCE v4 / 2014-05-17 / by goffy

tinymce.PluginManager.requireLangPack('xoops_quote');

tinymce.PluginManager.add('xoops_quote',function(e,url) {function t(){

e.windowManager.open({
	title : '_XOOPS_EDITOR_TINYMCE_QUOTE_DLG_TITLE',
	body:{
		type: 'textbox',
		name: 'xoops_quote',
		multiline:!0,
		minWidth:500,
		minHeight:300,
		},

		onSubmit:function(t){
		e.insertContent("<div class='xoopsQuote'>"+t.data.xoops_quote+"</div><br />")
		}}
	)}

e.addButton("xoops_quote",{
	title: '_XOOPS_EDITOR_TINYMCE_QUOTE_TITLE',
	tooltip: '_XOOPS_EDITOR_TINYMCE_QUOTE_TOOLTIP',
	image: url + '/img/quote.png',
	onclick:t
	})
});
